import { useState, useMemo, useCallback, useEffect } from "react";
import axios from "axios";
// import { useDispatch, useSelector } from 'react-redux';
import { API_URL, transformChannels, transformEgp, roundMinutes, isEmpty } from "./helpers";
import { useEpg } from "planby";
import { theme } from "./helpers/theme";

const globalStyles = `
	.planby {
		font-family: "Poppins" sans-serif;
	}
`;

export function useApp() {
	// const dispatch = useDispatch();
	const [channelsList, setChannelsList] = useState([]);
	const [channels, setChannels] = useState([]);
	const [epg, setEpg] = useState([]);
	const [isLoading, setIsLoading] = useState(false);

	function fetchAllChannels() {
		setIsLoading(true)
		axios.get(API_URL)
			.then(res => res.data) 
			.then(data => {
				console.log("data", data.response.channels)
				// if(res.status === 200){
				setChannelsList(data.response.channels);
				setIsLoading(false);
				// }
			})
	}

	// const { list: channelsList } = useSelector(state => state.channels);
	// const { list: epgList } = useSelector(state => state.epg);

	const date = new Date()
	const startDate = roundMinutes(date);	

	const channelsData = useMemo(() => channels, [channels]);
	const epgData = useMemo(() => epg, [epg]);

	const { getEpgProps, getLayoutProps } = useEpg({
		channels: channelsData,
		epg: epgData,
		dayWidth: 7200,
		sidebarWidth: 150,
		itemHeight: 80,
		isSidebar: true,
		isTimeline: true,
		isLine: true,
		startDate: startDate,
		isBaseTimeFormat: true,
		theme,
		globalStyles
	});

	// const handleFetchResources = useCallback(async () => {
	// 	setIsLoading(true);
	// 	const channelList = fetchAllChannels();
	// 	console.log("ðŸš€ ~ channelList", channelList)
	// 	// const epgList = await fetchEpgList();
	// 	// if (!isEmpty(channelList) || !channelList.length) {
	// 	// 	console.log("ya hay")
	// 	// 	const formatedChannels = transformChannels(channelList)
	// 	// 	const formatedEpg = transformEgp(channelList)
	// 	// 	console.log("ðŸš€ ~ file: useApp.js:53 ~ handleFetchResources ~ formatedChannels", formatedChannels)
	// 	// 	setChannels(formatedChannels)
	// 	// 	setEpg(formatedEpg);
	// 	setIsLoading(false);
	// 	// }
	// }, []);
	// console.log("ðŸš€ ~ file: useApp.js:49 ~ handleFetchResources ~ channels", channels)
	useEffect(() => {
		fetchAllChannels();
	}, []);

	useEffect(() => {
		// setIsLoading(true);

		if (channelsList.length !== 0) {
			console.log("ya hay", channelsList)
			// const formatedChannels = transformChannels(channelsList)
			// const formatedEpg = transformEgp(channelsList)
			// console.log("ðŸš€ ~ file: useApp.js:53 ~ handleFetchResources ~ formatedChannels", formatedChannels)
			// setChannels(formatedChannels)
			// setEpg(formatedEpg);
				// setIsLoading(false);
		}
	}, [channelsList]);

	return { getEpgProps, getLayoutProps, isLoading };
}
