import React from "react";
import { useDispatch, useSelector } from 'react-redux';
import { fetchChannels, fetchEpg, fetchAllChannels } from "./helpers";

import { useEpg } from "planby";

// Import theme
import { theme } from "./helpers/theme";

export function useApp() {
	const dispatch = useDispatch();
	const { list: channelsList } = useSelector(state => state.channels);
	const [channels, setChannels] = React.useState([]);
	const [epg, setEpg] = React.useState([]);
	const [isLoading, setIsLoading] = React.useState(false);

	const channelsData = React.useMemo(() => channels, [channels]);
	const epgData = React.useMemo(() => epg, [epg]);

	const { getEpgProps, getLayoutProps } = useEpg({
		channels: channelsData,
		epg: epgData,
		dayWidth: 7200,
		sidebarWidth: 100,
		itemHeight: 80,
		isSidebar: true,
		isTimeline: true,
		isLine: true,
		startDate: "2023/01/09",
		// endDate: "2023-01-109T24:00:00",
		isBaseTimeFormat: true,
		theme
	});

  const handleFetchResources = React.useCallback(async () => {
    setIsLoading(true);
    const epg = await fetchEpg();
    const channels = await fetchChannels();

	const transformChannels = (channels) => {		
		return channels.map(channel => {
			return {
				uuid: channel.id,
				type: "channel",
				title: channel.name,
				logo: channel.image,
				year: 2002
			}
		});

	}
	const handleGetEpg = () => {
		let epgList = [];
		for( let channel of channelsList ){
			const events = channel.events;
			events.forEach(event => {
				epgList.push({
					channelUuid: event.channel_id,
					id: event.id,
					image: "https://www.themoviedb.org/t/p/w1066_and_h600_bestv2/sjx6zjQI2dLGtEL0HGWsnq6UyLU.jpg",
					since: new Date(event.date_begin).toISOString(),
					till: new Date(event.date_end).toISOString(),
					title: event.name,
					description: event.description,
				})
			})

		}
		return epgList;
	}
	console.log("ðŸš€ ~ file: useApp.js:121 ~ handleFetchResources ~ canales", transformChannels(channelsList))
	console.log("ðŸš€ ~ file: useApp.js:121 ~ handleFetchResources ~ handleGetEpg", handleGetEpg(channelsList))

    setEpg(handleGetEpg(channelsList));
    // setChannels(channels);
	setChannels(transformChannels(channelsList));
    setIsLoading(false);
  }, [channelsList]);

	React.useEffect(() => {
		handleFetchResources();
	}, [handleFetchResources]);

	React.useEffect(() => {
		dispatch(fetchAllChannels())
	}, [dispatch]);

  return { getEpgProps, getLayoutProps, isLoading };
}
