import { useState } from "react";
import "./index.css";
import { Epg, Layout } from "planby";
import { useApp } from "./useApp";
import { Timeline, ChannelItem, ProgramItem, Header } from "./components";

function App() {
  const { isLoading, getEpgProps, getLayoutProps } = useApp();
  const [programData, setProgramData] = useState({});
  const [showEpgModal, setShowEpgModal] = useState(false);

  const handleMouseEnter = ({data}) => {
    setProgramData(data);
  }

  const handleShowEpgModal = () => {
    setShowEpgModal(!showEpgModal)
  }

  return (
    <div className="flex justify-center items-center w-full h-screen relative">
      <button 
        type="button" 
        onClick={handleShowEpgModal} 
        className="bg-[#ac0000] text-white py-4 px-10 rounded-md font-poppins font-bold text-lg transition-all hover:bg-[#a00000] hover:shadow-md hover:-translate-y-1"
      >
        Mostrar EPG 
      </button>

      {/* {showEpgModal && ( */}
      <div style={showEpgModal ? {display: 'block'} : {display: 'none'}}>
        <div className="fixed inset-0 w-full h-full">
          <Header programData={programData} handleShowEpgModal={handleShowEpgModal} />
          <div className="w-full h-[60vh] relative">
            <p className="absolute left-[65px] top-[20px] z-[99] text-white font-poppins font-medium">Hoy</p>
            <Epg isLoading={isLoading} style={{padding: 0}} {...getEpgProps()}>
              <Layout
                {...getLayoutProps()}
                renderTimeline={(props) => <Timeline {...props} />}
                renderProgram={({ program, ...rest }) => {
                  return (
                    <ProgramItem
                      key={program.data.id}
                      program={program}
                      onMouseEnter={handleMouseEnter}
                      {...rest}
                    />
                  );
                }}
                renderChannel={({ channel }) => (
                  <ChannelItem key={channel.uuid} channel={channel} />
                )}
              />
            </Epg>
          </div>
        </div>
      </div>
      {/* ) } */}

    </div>
  );
}

export default App;
