import React from 'react'
import PropTypes from 'prop-types'
import {isEmpty} from '../helpers';

const formatHours = (date) => {
    const splittedDate = date.toLocaleString().split(' ')[1].split(':').splice(0, 2)
    return `${splittedDate.join('.')}hs`
}

export const Header = ({ programData }) => {
    console.log(isEmpty(programData))
    const {title, since, till, duration, description } = programData;
    const dateSince = since && formatHours(new Date(since));
    const dateTill = till && formatHours(new Date(till));
    const [hh, mm] = duration && duration.split(':').splice(0,2);
    const programInfo = `${dateSince} a ${dateTill} ${duration ? `${hh}h ${mm}min` : ""}`;

    return (
        <div>
            {!isEmpty(programData) && <>
                <h1>{title}</h1>
                <h2>{programInfo}</h2>
                <p></p>
            </>}
        </div>
    )
}

Header.propTypes = {}
