import axios from "axios";
import { setChannelList } from '../store/slices/channels';
import { channels } from "./channels";
import { epg } from "./epg";

// const date = new Date(Date.now()).toISOString();
// const date = new Date().toISOString().slice(-24).replace(/\D/g,'').slice(0, 14);
const formatDate = (date) => {
    return date.toISOString().slice(-24).replace(/\D/g,'').slice(0, 14);
}
const date = new Date();
console.log("ðŸš€ ~ file: common.js:12 ~ date", date)
const dateSince = formatDate(date)
const dateTill = newDate(date.setDate(date.getDate() + 1))
// const dateStr = date.toISOString().substring(0, 10).replace(/-/g, "");
console.log("1", dateSince)
console.log("2", dateTill)
// console.log("ðŸš€ ~ file: common.js:7 ~ date", date)
const API_URL = 'https://mfwkweb-api.clarovideo.net/services/epg/channel?device_id=web&device_category=web&device_model=web&device_type=web&device_so=Chrome&format=json&device_manufacturer=generic&authpn=webclient&authpt=tfg1h3j4k6fd7&api_version=v5.93&region=guatemala&HKS=web61144bb49d549&user_id=54343080&date_from=20230109220000&date_to=20230110220000&quantity=20';


export const fetchChannels = async () =>
    new Promise((res) => setTimeout(() => res(channels), 400));

export const fetchEpg = async () =>
    new Promise((res) => setTimeout(() => res(epg), 500));

export const fetchAllChannels = () => async (dispatch) => {
    await axios.get(API_URL)
        .then((res) => {
            // console.log(res.data.response.channels)
            dispatch(setChannelList(res.data.response.channels));
        })
        .catch((err) => console.log(err))
}
