import { useState, useMemo, useCallback, useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { fetchAllChannels, toISOStringWithTimezone } from "./helpers";
import { useEpg } from "planby";

// Import theme
import { theme } from "./helpers/theme";

export function useApp() {
	const dispatch = useDispatch();
	const { list: channelsList } = useSelector(state => state.channels);
	const { list: epgList } = useSelector(state => state.epg);
	const [channels, setChannels] = useState([]);
	const [epg, setEpg] = useState([]);
	const [isLoading, setIsLoading] = useState(false);

	const date = new Date()
	const startDate = date.getUTCFullYear() + "/" +
    ("0" + (date.getUTCMonth()+1)).slice(-2) + "/" +
    ("0" + date.getUTCDate()).slice(-2);
	const startDate2 = toISOStringWithTimezone(new Date()).slice(0, 19)



	function roundMinutes(date) {
		date.setHours(date.getHours() + Math.round(date.getMinutes()/60));
		date.setMinutes(0, 0, 0); // Resets also seconds and milliseconds
		return toISOStringWithTimezone(date).slice(0, 19);
	}

	console.log("round", roundMinutes(date)); 

	console.log("startDate", startDate)
	console.log("startDate2", startDate2)

	const channelsData = useMemo(() => channels, [channels]);
	const epgData = useMemo(() => epg, [epg]);

	const { getEpgProps, getLayoutProps } = useEpg({
		channels: channelsData,
		epg: epgData,
		dayWidth: 7200,
		sidebarWidth: 100,
		itemHeight: 80,
		isSidebar: true,
		isTimeline: true,
		isLine: true,
		startDate: startDate,
		// endDate: "2023-01-10T22:00:00",
		isBaseTimeFormat: true,
		theme
	});

	const handleFetchResources = useCallback(async () => {
		setChannels(channelsList);
		setEpg(epgList);
	}, [channelsList, epgList]);

	useEffect(() => {
		dispatch(fetchAllChannels())
	}, [dispatch]);

	useEffect(() => {
		if(!channelsList.length && !epgList.length) {
			setIsLoading(true);
		} else {
			setIsLoading(false);
			handleFetchResources()
		}
	}, [handleFetchResources, channelsList, epgList]);

	return { getEpgProps, getLayoutProps, isLoading };
}
