import React, {useMemo, useEffect, useState} from "react";

// import { fetchChannels, fetchEpg } from "./helpers";
import { fetchAllChannels } from "./helpers";

import { useEpg } from "planby";

// Import theme
import { theme } from "./helpers/theme";

import { useDispatch, useSelector } from 'react-redux';

export function useApp() {

	const dispatch = useDispatch();

	const { list: channelsList } = useSelector(state => state.channels);
	const [epg, setEpg] = useState([]);
	const [isLoading, setIsLoading] = useState(false);

	const channelsData = useMemo(() => channelsList, [channelsList]);
	const epgData = useMemo(() => epg, [epg]);
	// console.log("ðŸš€ ~ file: useApp.js:23 ~ useApp ~ epgData", epgData)

	const handleGetEpg = () => {
		let epgList = [];
		for( let channel of channelsData ){
			const events = channel.events;
			events.forEach(event => {
				epgList.push({
					...event,
					since: new Date(event.date_begin).toISOString(),
					till: new Date(event.date_end).toISOString(),
					id: event.id,
					title: event.name,
					description: event.description,
					isYesterday: true,
					channelUuid: event.channel_id,
				})
			} )

		}
		return epgList;
	}

	const { getEpgProps, getLayoutProps } = useEpg({
		channels: channelsData,
		epg: epgData,
		dayWidth: 7200,
		sidebarWidth: 100,
		itemHeight: 80,
		isSidebar: true,
		isTimeline: true,
		isLine: true,
		startDate: "2022-10-18T00:00:00",
		endDate: "2022-10-18T24:00:00",
		isBaseTimeFormat: true,
		theme
	});

	useEffect(() => {
		setEpg(handleGetEpg())
	}, []);

	useEffect(() => {
		setIsLoading(true)
		dispatch(fetchAllChannels())
		setIsLoading(false)
	}, [dispatch, isLoading]);

	return { getEpgProps, getLayoutProps, isLoading };
}
