import { useState, useMemo, useCallback, useEffect } from "react";
// import { useDispatch, useSelector } from 'react-redux';
import { fetchAllChannels, transformChannels, transformEgp, roundMinutes, isEmpty } from "./helpers";
import { useEpg } from "planby";
import { theme } from "./helpers/theme";

const globalStyles = `
	.planby {
		font-family: "Poppins" sans-serif;
	}
`;

export function useApp() {
	// const dispatch = useDispatch();

	// const { list: channelsList } = useSelector(state => state.channels);
	// const { list: epgList } = useSelector(state => state.epg);

	const [channels, setChannels] = useState([]);
	const [epg, setEpg] = useState([]);
	const [isLoading, setIsLoading] = useState(false);

	const date = new Date()
	const startDate = roundMinutes(date);	

	const channelsData = useMemo(() => channels, [channels]);
	const epgData = useMemo(() => epg, [epg]);

	const { getEpgProps, getLayoutProps } = useEpg({
		channels: channelsData,
		epg: epgData,
		dayWidth: 7200,
		sidebarWidth: 150,
		itemHeight: 80,
		isSidebar: true,
		isTimeline: true,
		isLine: true,
		startDate: startDate,
		isBaseTimeFormat: true,
		theme,
		globalStyles
	});

	const handleFetchResources = useCallback(async () => {
		setIsLoading(true);
		const channelList = await fetchAllChannels();
		// const epgList = await fetchEpgList();
		if (!isEmpty(channelList) || !channelList.length) {
			console.log("ya hay")
			const formatedChannels = transformChannels(channelList)
			console.log("ðŸš€ ~ file: useApp.js:53 ~ handleFetchResources ~ formatedChannels", formatedChannels)
			setChannels(transformChannels(channelList))
			setEpg(transformEgp(channelList));
			setIsLoading(false);
		}
	}, [fetchAllChannels]);
	// console.log("ðŸš€ ~ file: useApp.js:49 ~ handleFetchResources ~ channels", channels)
	// useEffect(() => {
	// 	dispatch(fetchAllChannels())
	// }, [dispatch]);

	useEffect(() => {
		handleFetchResources();
	}, [handleFetchResources]);

	return { getEpgProps, getLayoutProps, isLoading };
}
