import React, {useCallback, useEffect, useState} from "react";

// import { fetchChannels, fetchEpg } from "./helpers";
import { fetchAllChannels } from "./helpers";

import { useEpg } from "planby";

// Import theme
import { theme } from "./helpers/theme";

import { useDispatch, useSelector } from 'react-redux';

export function useApp() {

	const dispatch = useDispatch();

	const { list: channelsList } = useSelector(state => state.channels);
	const [epg, setEpg] = React.useState([]);
	const [isLoading, setIsLoading] = useState(false);
	// const epg = [
	// 	{
	// 		"id":"6f3caa7f-5b11-4edb-998e-80d4baa03373",
	// 		"title":"The Book of Boba Fett",
	// 		"isYesterday":true,
	// 		"since":"2022-10-17T23:50:00",
	// 		"till":"2022-10-18T00:55:00",
	// 		"channelUuid":"16fdfefe-e466-4090-bc1a-57c43937f826",
	// 	},
	// 	{
	// 		"id":"4283023a-8bc8-453b-ba29-2af931c37e4d",
	// 		"title":"Two and a Half Men",
	// 		"isYesterday":true,
	// 		"since":"2022-10-17T23:10:00",
	// 		"till":"2022-10-18T01:35:00",
	// 		"channelUuid":"96aaf72c-b5ed-4ce4-937d-1912e4f8bf0d",
	// 	}
	// ]

	const channelsData = React.useMemo(() => channelsList, [channelsList]);
	const epgData = React.useMemo(() => epg, [epg]);

	console.log("ðŸš€ ~ file: useApp.js:23 ~ useApp ~ epgData", epgData)

	const handleGetEpg = () => {
		let epgList = [];
		for( let channel of channelsData ){
			const events = channel.events;
			events.forEach(event => {
				const since = new Date(event.date_begin).toISOString();
				const till = new Date(event.date_end).toISOString();
				epgList.push({
					...event,
					since,
					till
				})
			} )

		}
		return epgList;
	}

	const { getEpgProps, getLayoutProps } = useEpg({
		channels: channelsData,
		epg: epgData,
		dayWidth: 7200,
		sidebarWidth: 100,
		itemHeight: 80,
		isSidebar: true,
		isTimeline: true,
		isLine: true,
		// startDate: "2022-10-18T00:00:00",
		// endDate: "2022-10-18T24:00:00",
		// isBaseTimeFormat: true,
		theme
	});

	useEffect(() => {
		setEpg(handleGetEpg())
	}, []);

	useEffect(() => {
		setIsLoading(true)
		dispatch(fetchAllChannels())
		setIsLoading(false)
	}, [dispatch, isLoading]);

	return { getEpgProps, getLayoutProps, isLoading };
}
