	// 		console.log("ðŸš€ ~ file: useApp.js:53 ~ handleFetchResources ~ formatedChannels", formatedChannels)
	// 		setChannels(formatedChannels)
	// 		setEpg(formatedEpg);
	// 		setIsLoading(false);
	// 	}
	// }, [fetchAllChannels]);
	// console.log("ðŸš€ ~ file: useApp.js:49 ~ handleFetchResources ~ channels", channels)
	// useEffect(() => {
	// 	dispatch(fetchAllChannels())
	// }, [dispatch]);

	useEffect(() => {
		setIsLoading(true);

		// const epgList = await fetchEpgList();
		// if (!isEmpty(channelList) || !channelList.length) {
			console.log("ya hay", channelList)
			const formatedChannels = transformChannels(channelList)
			const formatedEpg = transformEgp(channelList)
			console.log("ðŸš€ ~ file: useApp.js:53 ~ handleFetchResources ~ formatedChannels", formatedChannels)
			setChannels(formatedChannels)
			setEpg(formatedEpg);
			setIsLoading(false);
		// }
	}, [channelList]);

	return { getEpgProps, getLayoutProps, isLoading };
}
