import axios from "axios";
import { setChannelList } from '../store/slices/channels';

const API_URL = 'https://mfwkweb-api.clarovideo.net/services/epg/channel?device_id=web&device_category=web&device_model=web&device_type=web&device_so=Chrome&format=json&device_manufacturer=generic&authpn=webclient&authpt=tfg1h3j4k6fd7&api_version=v5.93&region=guatemala&HKS=web61144bb49d549&user_id=54343080&date_from=20230108220000&date_to=20230109220000&quantity=200';

import { channels } from "./channels";
import { epg } from "./epg";

export const fetchChannels = async () =>
    new Promise((res) => setTimeout(() => res(channels), 400));

export const fetchEpg = async () =>
    new Promise((res) => setTimeout(() => res(epg), 500));

export const fetchAllChannels = () => (dispatch) => {
    axios.get(API_URL)
        .then((res) => {
            // console.log(res.data.response.channels)
            dispatch(setChannelList(res.data.response.channels));
        })
        .catch((err) => console.log(err))
}
