import { createSlice } from '@reduxjs/toolkit';

export const epgSlice = createSlice({
    name: 'epg',
    initialState: {
        list: [] //lista de epg
    },
    reducers: {
        setEpgList: (state, action) => {
            state.list = action.payload;
        }
    }
});

export const { setEpgList } = epgSlice.actions;
export default epgSlice.reducer;
