import {
    ProgramBox,
    ProgramContent,
    ProgramFlex,
    ProgramStack,
    ProgramTitle,
    ProgramText,
    useProgram
} from "planby";

export const ProgramItem = ({ program, ...rest }) => {

    const {
        styles,
        formatTime,
        set12HoursTimeFormat,
        isLive,
    } = useProgram({
        program,
        ...rest
    });

    const { data } = program;
    const { title, since, till } = data;

    const sinceTime = formatTime(since, set12HoursTimeFormat()).toLowerCase();
    const tillTime = formatTime(till, set12HoursTimeFormat()).toLowerCase();

    return (
    <ProgramBox width={styles.width} style={styles.position}>
        <ProgramContent width={styles.width}>
            <ProgramFlex>
                <ProgramStack>
                    <ProgramTitle>{title}</ProgramTitle>
                    <ProgramText>
                        {sinceTime} - {tillTime}
                    </ProgramText>
                </ProgramStack>
            </ProgramFlex>
        </ProgramContent>
    </ProgramBox>
    );
};
