import { useEffect } from 'react';
import axios from "axios";

import { useDispatch, useSelector } from 'react-redux';
// import { fetchAllChannels } from '../helpers/common';

import { setChannelList } from '../store/slices/channels';

const API_URL = 'https://mfwkweb-api.clarovideo.net/services/epg/channel?device_id=web&device_category=web&device_model=web&device_type=web&device_so=Chrome&format=json&device_manufacturer=generic&authpn=webclient&authpt=tfg1h3j4k6fd7&api_version=v5.93&region=guatemala&HKS=web61144bb49d549&user_id=54343080&date_from=20210812200256&date_to=20210813200256&quantity=200';

export const fetchAllChannels = () => (dispatch) => {
    axios.get(API_URL)
        .then((res) => {
            // console.log(res.data.response.channels)
            dispatch(setChannelList(res.data.response.channels));
        })
        .catch((err) => console.log(err))
}

export const fetchEpg = async () => {
    const { list: channels } = useSelector(state => state.channels);
    const dispatch = useDispatch();
    console.log(channels)
    useEffect(() => {
        dispatch(fetchAllChannels());
    }, [dispatch])
}
