import React from "react";
import { useDispatch, useSelector } from 'react-redux';
import { fetchChannels, fetchEpg, fetchAllChannels } from "./helpers";

import { useEpg } from "planby";

// Import theme
import { theme } from "./helpers/theme";

export function useApp() {
	const dispatch = useDispatch();

  const [channels, setChannels] = React.useState([]);
  const [epg, setEpg] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(false);

  const channelsData = React.useMemo(() => channels, [channels]);
  const epgData = React.useMemo(() => epg, [epg]);

  const { getEpgProps, getLayoutProps } = useEpg({
    channels: channelsData,
    epg: epgData,
    dayWidth: 7200,
    sidebarWidth: 100,
    itemHeight: 80,
    isSidebar: true,
    isTimeline: true,
    isLine: true,
    startDate: "2022-10-18T00:00:00",
    endDate: "2022-10-18T24:00:00",
    isBaseTimeFormat: true,
    theme
  });

  const handleFetchResources = React.useCallback(async () => {
    setIsLoading(true);
    const epg = await fetchEpg();
    console.log("ðŸš€ ~ file: useApp.js:115 ~ handleFetchResources ~ epg", epg)
    const channels = await fetchChannels();
    console.log("ðŸš€ ~ file: useApp.js:117 ~ handleFetchResources ~ channels", channels)
	const { list: channelsList } = useSelector(state => state.channels);
    console.log("ðŸš€ ~ file: useApp.js:121 ~ handleFetchResources ~ channelsList", channelsList)
    setEpg(epg);
    setChannels(channels);
    setIsLoading(false);
  }, []);

	React.useEffect(() => {
		handleFetchResources();
	}, [handleFetchResources]);

	React.useEffect(() => {
		setIsLoading(true)
		dispatch(fetchAllChannels())
		setIsLoading(false)
	}, [dispatch, isLoading]);

  return { getEpgProps, getLayoutProps, isLoading };
}
