import axios from "axios";
import { setChannelList } from '../store/slices/channels';
import { channels } from "./channels";
import { epg } from "./epg";

// const formatDate = (date) => {
//     console.log("ðŸš€ ~ file: common.js:8 ~ formatDate ~ date", date)
//     return date.toISOString().slice(-24).replace(/\D/g,'').slice(0, 14);
// }
const toISOStringWithTimezone = date => {
    const tzOffset = -date.getTimezoneOffset();
    const diff = tzOffset >= 0 ? '+' : '-';
    const pad = n => `${Math.floor(Math.abs(n))}`.padStart(2, '0');
    return date.getFullYear() +
        '-' + pad(date.getMonth() + 1) +
        '-' + pad(date.getDate()) +
        'T' + pad(date.getHours()) +
        ':' + pad(date.getMinutes()) +
        ':' + pad(date.getSeconds()) +
        diff + pad(tzOffset / 60) +
        ':' + pad(tzOffset % 60);
};

const date = new Date();
const dateSince = toISOStringWithTimezone(date)
const dateTill = toISOStringWithTimezone(new Date(date.setDate(date.getDate() + 1)));

const API_URL = `https://mfwkweb-api.clarovideo.net/services/epg/channel?device_id=web&device_category=web&device_model=web&device_type=web&device_so=Chrome&format=json&device_manufacturer=generic&authpn=webclient&authpt=tfg1h3j4k6fd7&api_version=v5.93&region=guatemala&HKS=web61144bb49d549&user_id=54343080&date_from=${dateSince}&date_to=${dateTill}&quantity=200`;


export const fetchChannels = async () =>
    new Promise((res) => setTimeout(() => res(channels), 400));

export const fetchEpg = async () =>
    new Promise((res) => setTimeout(() => res(epg), 500));

export const fetchAllChannels = () => async (dispatch) => {
    await axios.get(API_URL)
        .then((res) => {
            dispatch(setChannelList(res.data.response.channels));
        })
        .catch((err) => console.log(err))
}
